float4 xgenFacingCameraVSToGSPc(float3 Pm, float4x4 worldViewProjection, float depthPriority)
{
    float4 pc = mul(worldViewProjection, float4(Pm, 1.0f));
    pc.z -= pc.w * 2.0f * depthPriority;
    return pc;
}


vec4 xgenFacingCameraVSToGSPc(vec3 Pm, mat4 worldViewProjection, float depthPriority)
{
    vec4 pc = mul(worldViewProjection, vec4(Pm, 1.0f));
    pc.z -= pc.w * 2.0f * depthPriority;
    return pc;
}


float4 xgenFacingCameraVSToGSPc(float3 Pm, float4x4 worldViewProjection, float depthPriority)
{
    float4 pc = mul(float4(Pm, 1.0f), worldViewProjection);
    pc.z -= pc.w * depthPriority;
    return pc;
}
